{!REQUIRESCRIPT("/soap/ajax/32.0/connection.js")} 
{!REQUIRESCRIPT("/soap/ajax/32.0/apex.js")} 
{!REQUIRESCRIPT("/resource/ActOn__ActOnLib")}

invokeWithActOnServerUrl( function(url) 
{

var records = {!GETRECORDIDS( $ObjectType.Lead )}; 
if (records == null || records[0] == null) 
{
alert("Please select at least one record.");
} 
else 
{
// create the form. Set it up to POST the transaction
 var f = document.createElement('form');
 f.action=url + '/acton/sforce/sendSforceContactOrLead.jsp';
 f.method = 'post';
 f.target = 'actonWin';


// add the session id as a parameter
 var i = document.createElement('input');
 i.id = 'session';
 i.name = 'session';
 i.type = 'hidden';
 i.value = '{!$Api.Session_ID}';
 f.appendChild(i);


// add the server location as a parameter
 i = document.createElement('input');
 i.id = 'server';
 i.name = 'server';
 i.type = 'hidden';
 i.value = '{!$Api.Partner_Server_URL_190}';
 f.appendChild(i);


// Get the Lead IDs that were checked
// add the idArray as a parameter
 i = document.createElement('input');
 i.id = 'id';
 i.name = 'id';
 i.type = 'hidden';
 i.value =records;
 f.appendChild(i);


// add the form to the document.
 document.body.appendChild(f);

// submit the form
window.open ('','actonWin','modal=yes,dependent=yes,resizable=yes,scrollbars=yes,toobar=no,location=no,status=no,width=1200,height=768'); 
 f.submit();
}
});
