WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/40.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/40.0/apex.js")}
var records = {!GETRECORDIDS($ObjectType.Lead)}; 
var aid = "{!JSENCODE($Organization.Id)}"; 
var uid = "{!JSENCODE($User.Id)}"; 
var phoneField = "Phone"; 
var list = records.join(); 
var type = "Lead"; 
var nameField = "Name"; 

if (records[0] == null) { 
alert("Please select at least one Account to submit."); 
} 
else{ 

var result = sforce.apex.execute("DialSource.addToQueue","httpPOSTlist", 
{phoneField : phoneField, nameField : nameField, recordIDs : list, objectType : type, uid : uid, aid : aid}); 
window.alert('Records successfully queued'); 
} 

if(result !=""){ 
alert("Records Not Added to Queue: \n" + result); 
}
